window.onload=function(){
	//此方法可判断移动端与PC端。
	var browser={     
		versions:function(){            
		var u = navigator.userAgent, app = navigator.appVersion;
			return {
				//移动终端浏览器版本信息                 
				trident: u.indexOf('Trident') > -1, //IE内核                 
				presto: u.indexOf('Presto') > -1, //opera内核                 
				webKit: u.indexOf('AppleWebKit') > -1, //苹果、谷歌内核                 
				gecko: u.indexOf('Gecko') > -1 && u.indexOf('KHTML') == -1, //火狐内核                 
				mobile: !!u.match(/AppleWebKit.*Mobile.*/)||!!u.match(/AppleWebKit/), //是否为移动终端                 
				ios: !!u.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/), //ios终端                 
				android: u.indexOf('Android') > -1 || u.indexOf('Linux') > -1, //android终端或者uc浏览器                 
				iPhone: u.indexOf('iPhone') > -1 || u.indexOf('Mac') > -1, //是否为iPhone或者QQHD浏览器                 
				iPad: u.indexOf('iPad') > -1, //是否iPad                 
				webApp: u.indexOf('Safari') == -1 //是否web应该程序，没有头部与底部             
			};          
		}(), language:(navigator.browserLanguage || navigator.language).toLowerCase() 
	} 
	if( !(browser.versions.android || browser.versions.iPhone || browser.versions.iPad)){ 
		console.log("PC浏览页面");
	}
	//判定移动端或者PC端
	function IsPC(){  
		var userAgentInfo = navigator.userAgent;  
		var Agents = new Array("Android", "iPhone", "SymbianOS", "Windows Phone", "iPad", "iPod");  
		var flag = true;  
		for (var v = 0; v < Agents.length; v++) {  
		  if (userAgentInfo.indexOf(Agents[v]) > 0) { flag = false; break; }  
		}  
		return flag;  
	}
	if(IsPC){
		console.log("PC浏览页面");
	}
	//禁用F12
	// document.onkeydown = function(){
	// 	if(window.event && window.event.keyCode == 123) {
	// 		alert("F12被禁用");
	// 		event.keyCode=0;
	// 		event.returnValue=false;
	// 	}
	// }
	// //禁用鼠标右键
	// document.onmousedown=function(oEvent){
	// 	if(window.event)
	// 		oEvent=window.event;
	// 	if(oEvent.button==2)
	// 		alert("鼠标右键不可用");
    //   }

    //正常逻辑代码
    var tag = document.getElementById("tags");
    setTimeout(function(){
    	tag.className="rotation";
    },5000);
    var musicTag = document.getElementById("musicTag");
    var musicSource = document.getElementById("musicSource");
    musicTag.addEventListener("touchstart",function(event){
    	if(!musicSource.paused){
    		musicSource.pause();
    		musicTag.getElementsByTagName("img")[0].setAttribute("src","img/use/play.png");
    	}else{
    		musicSource.play();
    		musicTag.getElementsByTagName("img")[0].setAttribute("src","img/use/pause.png");
    	}
    },false);
    musicTag.addEventListener("ended",function(event){
    	musicTag.getElementsByTagName("img")[0].setAttribute("src","img/use/play.png");
    },false);
}